var swap = require('./swap');

exports = function(arr, cmp) {
    cmp = cmp || comparator;

    for (var i = 1, len = arr.length; i < len; i++) {
        for (var j = i; j > 0; j--) {
            if (cmp(arr[j], arr[j - 1]) < 0) {
                swap(arr, j, j - 1);
            } else {
                break;
            }
        }
    }

    return arr;
};

function comparator(a, b) {
    return a - b;
}

module.exports = exports;
